INSERT INTO `qr_fortune` VALUES (1413, '凶', '☆☆☆☆☆☆☆', '虽倾全力，难望成功，此数大凶，最好改名', '即使全力以赴，却没有取得成功。导致失败的原因是现时的待人处事方式有问题，要改变这个现状，要自我反省，改变一些令人厌恶的恶习，方可取得成功。');
INSERT INTO `qr_fortune` VALUES (1414, '大吉+财运', '★★★★★★☆', '谦恭做事，必得人和，大事成就，一定兴隆', '福寿拱照德望高，财子寿全又温和，慈祥好善可恭敬，富贵繁荣得惠泽。财源特佳金钱有餘，离出生之地而往大都市求谋必得更发达。外缘殊胜，容易得人之助力及予人好印象。');
INSERT INTO `qr_fortune` VALUES (1415, '大吉', '★★★★★☆☆', '万物化育，繁荣之象，专心一意，始能成功', '富贵荣达得显甯，子孙繁茂福绵绵，一身平安富益寿，福禄双全享千锺。');
INSERT INTO `qr_fortune` VALUES (1416, '凶', '★☆☆☆☆☆☆', '忍得苦难，必有后福，是成是败，惟靠坚毅', '只要有坚毅的决心，肯下功夫肯吃苦，多么难的事也能做成功。成功了，福也是必然相随。');
INSERT INTO `qr_fortune` VALUES (1417, '凶带吉', '★★★☆☆☆☆', '名虽可得，利则难获，艺界发展，可望成功', '可能有名气，却未能用这名气带来财运，可考虑从事娱乐或艺术来业，把名气转化成事业中一部分。');
INSERT INTO `qr_fortune` VALUES (1418, '中吉', '★★★★★☆☆', '专心经营，和气致祥，排除万难，必获成功', '刚毅果断除万难，独立权威志气安，内外和好兼柔性，温和养德耀吉星。很勤勉求上进，又很有耐力，在技术界或艺能界，是最可能成功之数也。但此数因少得人和，而略感孤独些。');
INSERT INTO `qr_fortune` VALUES (1419, '凶', '★★☆☆☆☆☆', '波澜起伏，千变万化，凌驾万难，必可成功', '局势不稳定，变化极多，任何困难都难不到你，最终您将一定会成功，且成就不小，关键是您能否在多变的艰难环境下坚持到最后。');
INSERT INTO `qr_fortune` VALUES (1420, '吉带凶', '★☆☆☆☆☆☆', '盛衰参半，外祥内苦，先吉后凶，先凶后吉', '一生好运与恶运都会让您遇上，当外表风光时，可能您的内心正受到非常大的困苦。前半生可能过得很苦，但后半生会出现转运，生活变得很美好；也有可能是前半生过得很风光，而后半生却很潦倒。');
INSERT INTO `qr_fortune` VALUES (1421, '吉', '★★★★☆☆☆', '吉运自来，能享盛名，把握机会，必获成功', '人生路上机会众多，只要把握好一次机会，将会改写您的人生。');
INSERT INTO `qr_fortune` VALUES (1422, '凶', '☆☆☆☆☆☆☆', '薄弱无力，孤立无摇，外祥内苦，谋事难成', '人生路上诸事不顺，自身能力之限制，经常迫于无耐。由于性格或人际关系上的问题，难遇贵人。经常一人处理所有事情，即使有天大的能力，总会遇到不能解决的问题，而这些问题会导致事业的失败。');
INSERT INTO `qr_fortune` VALUES (1423, '中吉', '★★★★★☆☆', '天时地利，再得人格，讲信修睦，即可成功', '资性英敏有奇能，怪癖不和害前程，修身涵养与仁和，奏功获得大鸿图。需要注重个人私生活，可免因桃色韵事贻误前程。');
INSERT INTO `qr_fortune` VALUES (1424, '吉带凶', '★☆☆☆☆☆☆', '事业不专，十九不成，专心进取，可望成功', '对事业不专注者，很难有所成就，有了进取心，对事业专心致志才有成功的希望！');
INSERT INTO `qr_fortune` VALUES (1425, '大吉', '★★★★★★☆', '阴阳和合，生意欣荣，名利双收，后福重重', '福禄寿长阴阳和，心身健全是英豪，名利双收富荣达，乃是世上福德人。财源特佳金钱有餘，离出生之地而往大都市求谋必得更发达。');
INSERT INTO `qr_fortune` VALUES (1426, '凶', '☆☆☆☆☆☆☆', '波澜重迭，常陷穷困，动不如静，有才无命', '越是对命运的抗争，越是使自已陷入困境，常常想靠自已改变命运，却一次次使自已的生活走入穷困，还不如听天由命，安于本份。');
INSERT INTO `qr_fortune` VALUES (1427, '中吉', '★★★★★☆☆', '排除万难，有贵人助，把握时机，可得成功', '突破万难权威高，刚性固执如英豪，须事谨慎守和平，可得大功奏业成。很勤勉求上进，又很有耐力，在技术界或艺能界，是最可能成功之数也。但此数因少得人和，而略感孤独些。');
INSERT INTO `qr_fortune` VALUES (1428, '凶', '☆☆☆☆☆☆☆', '动摇不安，常陷逆境，不得时运，难得利润', '坚持是您现时最缺乏的品质，由于没有坚定的目标，会使您陷入相当困苦的境地。而当获得成功时，却发现时不与我，外部环境已使得竞争激烈导致利润下降。');
INSERT INTO `qr_fortune` VALUES (1429, '大吉+财运', '★★★★★★☆', '草木逢春，雨过天晴，渡过难关，即获成功', '先见机明察佳期，意志坚固好运时，功名利达福禄全，一世荣隆乐绵绵。');
INSERT INTO `qr_fortune` VALUES (1430, '凶', '★☆☆☆☆☆☆', '秋草逢霜，怀才不遇，忧愁怨苦，事不如意', '秋天的枯草已经赶上了冰冷的寒霜，而我胸怀才学但生不逢时，难以施展，人生尽是这些忧愁、埋怨、苦衷，人生之事都不如意。');
INSERT INTO `qr_fortune` VALUES (1431, '凶带吉', '★★☆☆☆☆☆', '一成一败，一盛一衰，惟靠谨慎，可守成功', '成 败， 盛 衰这都是相对的，有成功就会有失败，有强盛就会有衰落这都只是时间问题，只有坚定的信念对能获得一定的成功但并不能够永久。');
INSERT INTO `qr_fortune` VALUES (1432, '凶', '☆☆☆☆☆☆☆', '此数大凶，破产之象，宜速改变，以避厄运', '不祥之兆，财产有可能一夜尽失，应尽快改变现在的处事和投资方式，避免厄运。');
